import spock.lang.Specification

class Task1Test extends Specification {

    def "given empty input Santa stays on the zero floor"() {
        expect:
        moveSanta(null) == 0
        moveSanta("") == 0
    }

    def "Santa can move"() {
        expect:
        moveSanta("(") == 1
        moveSanta(")") == -1
    }

    def "Santa can move twice"() {
        expect:
        moveSanta("((") == 2
        moveSanta("))") == -2
    }

    def "Santa can move in different dirrections"() {
        expect:
        moveSanta("()") == 0
        moveSanta(")()(") == 0
    }

